import React from "react";
import { BrowserRouter, Route, Routes, useLocation } from "react-router-dom";
import { AnimatePresence, motion } from "framer-motion"; // framer-motion import 추가

// 페이지 전환 애니메이션을 적용하는 컴포넌트
const PageTransition = ({ children }) => (
    <motion.div
        initial={{ opacity: 0 }} // 페이지 시작 시 투명
        animate={{ opacity: 1 }} // 점차 불투명
        exit={{ opacity: 0 }}    // 페이지 종료 시 투명
        transition={{ duration: 0.5 }} // 애니메이션 지속 시간
    >
        {children}
    </motion.div>
);

const AnimatedRoutes = () => {
    const location = useLocation();

    return (
        <AnimatePresence mode="wait">
            <Routes location={location} key={location.pathname}>
                {/* 예시 경로 */}
                <Route path="/" element={<PageTransition><h1>Home Page</h1></PageTransition>} />
                <Route path="/about" element={<PageTransition><h1>About Page</h1></PageTransition>} />
            </Routes>
        </AnimatePresence>
    );
};

export default AnimatedRoutes;
